mapper.update:
update  sport_db0.activity
set start_time = #{start_time},
    end_time = #{end_time},
    status = 1,
    reason = #{reason}
where id = #{at_server_id};

update  sport_db0.activity
set status = 3,
  reason = #{reason}
where id = #{at_server_id};

mapper.select:
select user_id
from sport_db0.user_join_activity
where activity_id = #{at_server_id};

mapper.update:
update sport_db0.activity
set
    join_num = join_num + 1,
    hot_value = hot_value + join_num * 50
where id = #{at_server_id};

insert into
    sport_db0.user_join_activity(
    activity_id,
    user_id
    )
values(
    #{at_server_id},
    #{user_id}
    );
